const tinycolor = require("tinycolor2");

// Define a color in HEX format
const color = tinycolor("#FABADA");

// Convert to different formats
console.log("RGB:", color.toRgbString()); // "rgb(250, 186, 218)"
console.log("RGBA:", color.setAlpha(0.5).toRgbString()); // "rgba(250, 186, 218, 0.5)"
console.log("HSL:", color.toHslString()); // "hsl(334, 86%, 85%)"
console.log("HEX:", color.toHexString()); // "#fabada"

// Generate multiple stops
console.log("Lightest:", color.lighten(40).toRgbString());
console.log("Lighter:", color.lighten(30).toRgbString());
console.log("Light:", color.lighten(20).toRgbString());
console.log("Dark:", color.darken(20).toRgbString());
console.log("Darker:", color.darken(30).toRgbString());
console.log("Darkest:", color.darken(40).toRgbString());
