const fs = require("fs");
const tinycolor = require("tinycolor2");
const readline = require("readline");

const rl = readline.createInterface({
  input: process.stdin,
  output: process.stdout
});

rl.question("Enter HEX colors separated by commas: ", (input) => {
  const colors = input.split(",").map(hex => hex.trim());
  const colorTokens = {};

  colors.forEach(hex => {
    const color = tinycolor(hex);
    colorTokens[hex] = {
      formats: {
        RGB: color.toRgbString(),
        RGBA: color.setAlpha(0.5).toRgbString(),
        HSL: color.toHslString(),
        HEX: color.toHexString()
      },
      stops: {
        Lightest: color.lighten(40).toRgbString(),
        Lighter: color.lighten(30).toRgbString(),
        Light: color.lighten(20).toRgbString(),
        Dark: color.darken(20).toRgbString(),
        Darker: color.darken(30).toRgbString(),
        Darkest: color.darken(40).toRgbString()
      }
    };
  });

  // Write to a JSON file
  fs.writeFileSync("tokens.json", JSON.stringify(colorTokens, null, 2));
  console.log("Color data has been saved to tokens.json");
  rl.close();
});
